export const getOrderStatusColor = status => {
  switch (status) {
    case 'completed':
      return 'success';
    case 'synced':
      return 'primary';
    case 'pending':
      return 'warning';
    default:
      return 'medium';
  }
};
export const getOrderStatusLabel = status => {
  switch (status) {
    case 'completed':
      return 'Completado';
    case 'synced':
      return 'Registrado';
    case 'pending':
      return 'Pendiente';
    default:
      return 'Estado';
  }
};
export const getOrderItemsCount = order => {
  return order.items.reduce((acc, item) => acc + item.quantity, 0);
};
export const getOrderShortId = id => {
  return id && id.length > 6 ? id.slice(-6) : id;
};
